import styled from "styled-components";
import { useState } from "react";
import { Link } from "react-router-dom";
// import Question from "./Question.js";
// import Sidebar from "./Sidebar.js";

function Body({ data }) {
  function handleClick(event, id) {}
  const [now, setNow] = useState(1);
  function nowHandler(event) {
    if (
      event.target.textContent === "Prev" &&
      event.target.textContent !== "1"
    ) {
      setNow(now - 1);
    } else if (
      event.target.textContent === "Next" &&
      event.target.textContent !== dummyPagenation.pageInfo.totalPages
    ) {
      setNow(now + 1);
    } else if (Number(event.target.textContent)) {
      setNow(Number(event.target.textContent));
    } else {
      setNow(now);
    }
  }
  let dummyPagenation = {
    data: [
      {
        questionId: 1,
        questionTitle: "제목",
        questionContent: "내용",
        memberId: "작성자id",
      },
      {
        questionId: 2,
        questionTitle: "제목1",
        questionContent: "내용2",
        memberId: "작정사Id",
      },
    ],
    pageInfo: {
      page: 1,
      size: 10,
      totalElements: 200,
      totalPages: 20,
    },
  };

  let pages = [];
  if (Number(now) === 1) {
    pages = [];
    for (
      let i = 1;
      i <= Math.min(5, dummyPagenation.pageInfo.totalPages);
      i++
    ) {
      pages.push(i);
    }
    if (dummyPagenation.pageInfo.totalPages > 5) {
      pages.push("...");
      pages.push(dummyPagenation.pageInfo.totalPages);
      pages.push("Next");
    } else if (dummyPagenation.pageInfo.totalPages > 1) {
      pages.push("Next");
    }
  } else if (Number(now) <= 4) {
    pages = ["Prev"];
    for (
      let i = 1;
      i <= Math.min(5, dummyPagenation.pageInfo.totalPages);
      i++
    ) {
      pages.push(i);
    }
    if (dummyPagenation.pageInfo.totalPages > 5) {
      pages.push("...");
      pages.push(dummyPagenation.pageInfo.totalPages);
      pages.push("Next");
    } else if (dummyPagenation.pageInfo.totalPages > Number(now)) {
      pages.push("Next");
    }
  } else if (Number(now) >= 5) {
    pages = ["Prev", 1, "..."];
    for (
      let i = Number(now) - 2;
      i <= Math.min(Number(now) + 2, dummyPagenation.pageInfo.totalPages);
      i++
    ) {
      pages.push(i);
    }
    if (dummyPagenation.pageInfo.totalPages === Number(now)) {
    } else if (dummyPagenation.pageInfo.totalPages === Number(now) + 3) {
      let anotherPage = pages.filter((el) => el !== Number(now) + -2);
      pages = [...anotherPage];
      pages.push(Number(now) + 3);
      pages.push("Next");
    } else if (dummyPagenation.pageInfo.totalPages > Number(now) + 3) {
      pages.push("...");
      pages.push(dummyPagenation.pageInfo.totalPages);
      pages.push("Next");
    } else if (dummyPagenation.pageInfo.totalPages > Number(now)) {
      pages.push("Next");
    }
  }

  return (
    <Main>
      {/* <Sidebar /> */}
      <div className="body">
        <div className="body-top">
          <h1>All Questions</h1>
          <div>
            <Link to="/create">
              <button className="askQuestion">Ask Question</button>
            </Link>
          </div>
        </div>
        <div className="body-middle">
          <div className="questionNubers">22,935,667 questions</div>
          <div>
            <div className="tab-filter">
              <div className="body-middle-tab">
                <a className="tabs">
                  <div>Newest</div>
                </a>
                <a className="tabs">
                  <div>Active</div>
                </a>
                <a className="tabs">
                  <div>Bountied 317</div>
                </a>
                <a className="tabs">
                  <div>Unanswered</div>
                </a>
                <button className="tabs">more ▼</button>
              </div>
              <div className="filter-box">
                <button className="filter">
                  <svg
                    width="18"
                    height="18"
                    viewBox="0 0 18 18"
                    fill="#39739d"
                  >
                    <path d="M2 4h14v2H2V4Zm2 4h10v2H4V8Zm8 4H6v2h6v-2Z"></path>
                  </svg>
                  <span>filter</span>
                </button>
              </div>
            </div>
          </div>
        </div>
        {/* {data !== null ? (
          <div className="body-main">
            {data.map((data) => (
              <Question
                data={data}
                key={data.questionId}
                handleClick={handleClick}
              />
            ))}
          </div>
        ) : null} */}
        <div>
          <div className="pagenation">
            {pages.map((el) => (
              <a
                onClick={nowHandler}
                id={el}
                className={`${el === "..." ? "none" : "pageBox"} ${
                  el === now ? "now" : ""
                }`}
              >
                {el}
              </a>
            ))}
          </div>
        </div>
      </div>
    </Main>
  );
}

export const Main = styled.div`
  display: flex;
  justify-content: center;
  width: 100%;
  overflow: hidden;

  .body {
    border-left: 1px, solid, gray;
    /* background-color: rgba(255, 50, 50, 0.3); */
    width: 60vw;
    border-left: 1px solid #d6d9dc;
  }

  h1 {
    font-size: 27px;
    font-weight: 400;
    margin-left: 20px;
  }

  .body-top {
    width: 727px;
    height: 60px;
    display: flex;
    justify-content: space-between;

    button {
      margin-top: 20px;
      background-color: #1095fa;
      border-radius: 5px;
    }
  }
  .body-middle {
    display: flex;
    height: 50px;
    .questionNubers {
      width: 260px;
      display: flex;
      align-items: center;
      margin-left: 20px;
    }
    .tab-filter {
      display: flex;
    }
    .body-middle-tab {
      display: flex;
      margin: 5px;
    }
    .tabs {
      font-size: 12px;
      color: #525960;
      border: 1px solid #9fa6ad;
      margin: 0 -1px -1px 0;
      padding: 10px 10px 10px 10px;
    }
    .filter-box {
      display: flex;
      align-items: center;
    }
    .filter {
      background-color: #e1ecf4;
      color: #2c5877;
      border: 1px solid #7aa7c7;
      border-radius: 3px;
      height: 39px;
      width: 69px;
      margin-left: 10px;
      padding: 10px 10px 10px 10px;
      span {
        vertical-align: 3px;
      }
    }
  }
  .askQuestion {
    font-size: 13px;
    line-height: 15px;
    text-decoration: none solid rgb(255, 255, 255);
    text-align: center;
    white-space: nowrap;
    word-spacing: 0px;

    background-color: #0074cc;
    background-position: 0% 0%;
    color: #ffffff;

    height: 37.8px;
    width: 100px;
    border: 1px solid #ffffff;
    padding: 10px 10px 10px 10px;

    cursor: pointer;
  }
  .pagenation {
    margin: 20px;
    .pageBox {
      font-size: 13px;
      line-height: 25px;
      height: 27px;
      width: 25px;
      border: 1px solid #babfc4;
      border-radius: 3px;
      margin: 0 2px 0 2px;
      padding: 0 8px 0 8px;
    }
    .now {
      background-color: #f48225;
      color: white;
      border: 1px solid #f48225;
    }
    .none {
      font-size: 13px;
      line-height: 25px;
      height: 27px;
      width: 25px;
      margin: 0 2px 0 2px;
      padding: 0 8px 0 8px;
    }
  }
`;

export default Body;
