              해산물
            </div>
            <div className="tag" onClick={tagHandler}>
              채소/야채
            </div>
            <div className="tag" onClick={tagHandler}>
              과일
            </div>
            <div className="tag" onClick={tagHandler}>
              유제품
            </div>
            <div className="tag" onClick={tagHandler}>
              음료
            </div>
            <div className="tag" onClick={tagHandler}>
              가공품
            </div>
            <div className="tag" onClick={tagHandler}>
              완제품
            </div>
            <div className="tag" onClick={tagHandler}>
              조미료/양념
            </div>
            <div className="tag" onClick={tagHandler}>
              기타
            </div>
          </div>
        </div>
        <div className="datas">
          <div className="data">
            <h2>수량</h2>
            <input></input>
          </div>
          <div className="data">
            <h2>유통기한</h2>
            <input type="date" value="2022-10-30"></input>
          </div>
          {/* <div className="data">
            <h2>소비기한</h2>
            <input></input>
          </div> */}
        </div>
        <div className="bottomLink">
          <Link to="/colder" className="bottomButton">
            냉장실에 추가하기 ╋
          </Link>
          <Link to="/freezer" className="bottomButton">
            냉동실에 추가하기 ╋
          </Link>
        </div>
      </div>
    </Main>
  );
}

export const Main = styled.div`
  height: 92.6vh;
  display: flex;
  justify-content: center;
  align-items: flex-end;
  .container {
    color: white;
